import RPi.GPIO as GPIO
from gpiozero import Button
from signal import pause
import subprocess
import sys

button = Button(6)





def turn_off_safety(program, exit_code=0):
    
    # Start main script
    subprocess.Popen(program)
    # close this script
    sys.exit(exit_code)
    


while True:

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
	if button.is_pressed: 
		turn_off_safety(['python', 'object-ident.py'])
